import React, { useState } from 'react';
import { Form } from 'react-bootstrap';

// --- Color Palette and Constants ---
const COLORS = {
  HEADER_BROWN: '#8B7355',
  LIGHT_TAN: '#C4B5A0',
  DARK_NAVY: '#4A5568',
  SELECTED_BROWN_TAN: '#8B7355', // Deeper shade for Tan buttons
  SELECTED_NAVY: '#2c3543',     // Deeper shade for Navy buttons
  BASE_BUTTON_TAN: '#C4B5A0',
  BASE_BUTTON_NAVY: '#4A5568',
  TEXT_GRAY: '#4A5568',
  LIGHT_GRAY_TEXT: '#A0AEC0',
  WHITE: 'white',
  BLACK: 'black',
  BLUE_BORDER: '#5C7094',
  ERROR_RED: '#C97C7C',
};

