import { useState } from 'react';
import { Container, Card, Button, Modal } from 'react-bootstrap';

const mockTeammates = [
  { id: 1, name: "Ben Holt" },
  { id: 2, name: "Zack Mitchell" },
  { id: 3, name: "Ashley Gutierrez" }
];

const evaluationSections = [
  {
    title: "Facilitates Team Climate",
    color: "tan",
    questions: [
      { key: 'climate1', text: 'Contributed to a Team Climate' },
      { key: 'climate2', text: 'Displayed a positive attitude' },
      { key: 'climate3', text: 'Acknowledged contributions' },
      { key: 'climate4', text: 'Demonstrated respect' },
      { key: 'climate5', text: 'Encouraged participation' }
    ]
  },
  {
    title: "Facilitates Contribution of Others",
    color: "navy",
    questions: [
      { key: 'contrib1', text: 'Drew out ideas and opinions' },
      { key: 'contrib2', text: 'Built on ideas of others' },
      { key: 'contrib3', text: 'Offered constructive criticism' },
      { key: 'contrib4', text: 'Acknowledged valid points' },
      { key: 'contrib5', text: 'Encouraged debate' }
    ]
  },
  {
    title: "Facilitates Peer Debate",
    color: "tan",
    questions: [
      { key: 'debate1', text: 'Organized and synthesized ideas' },
      { key: 'debate2', text: 'Clarified opposing views' },
      { key: 'debate3', text: 'Highlighted key issues' },
      { key: 'debate4', text: 'Kept discussion on track' },
      { key: 'debate5', text: 'Encouraged resolution' }
    ]
  },
  {
    title: "Facilitates Execution of Work",
    color: "tan",
    questions: [
      { key: 'exec1', text: 'Helped set goals and objectives' },
      { key: 'exec2', text: 'Contributed to task planning' },
      { key: 'exec3', text: 'Completed assigned work' },
      { key: 'exec4', text: 'Met deadlines consistently' },
      { key: 'exec5', text: 'Delivered quality work' }
    ]
  },
  {
    title: "Expects Team Climate",
    color: "purple",
    questions: [
      { key: 'expect1', text: 'Expected positive contributions' },
      { key: 'expect2', text: 'Expected respectful behavior' },
      { key: 'expect3', text: 'Expected active participation' },
      { key: 'expect4', text: 'Expected shared responsibility' },
      { key: 'expect5', text: 'Expected collaborative spirit' }
    ]
  },
  {
    title: "Manages Personal Quality",
    color: "tan",
    questions: [
      { key: 'quality1', text: 'Demonstrated self-awareness' },
      { key: 'quality2', text: 'Managed time effectively' },
      { key: 'quality3', text: 'Sought continuous improvement' },
      { key: 'quality4', text: 'Adapted to feedback' },
      { key: 'quality5', text: 'Maintained professionalism' }
    ]
  }
];

function RatingButton({ value, selected, onClick, color }) {
  const colorStyles = {
    tan: {
      base: 'bg-[#C4B5A0] border-[#C4B5A0]',
      selected: 'bg-[#8B7355] border-[#8B7355]'
    },
    navy: {
      base: 'bg-[#4A5568] border-[#4A5568]',
      selected: 'bg-[#1a202c] border-[#1a202c]'
    },
    purple: {
      base: 'bg-[#9F7AEA] border-[#9F7AEA]',
      selected: 'bg-[#6B46C1] border-[#6B46C1]'
    }
  };

  const colorClass = selected ? colorStyles[color].selected : colorStyles[color].base;

  return (
    <button
      onClick={onClick}
      className={`${colorClass} text-white px-4 py-2 rounded text-sm font-medium transition-all hover:opacity-80`}
    >
      {value}
    </button>
  );
}

function QuestionRow({ question, value, onChange, color }) {
  return (
    <div className="mb-3">
      <div className="text-sm text-gray-700 mb-2">{question.text}</div>
      <div className="flex gap-2">
        {[0, 1, 2, 3, 4].map((rating) => (
          <RatingButton
            key={rating}
            value={rating}
            selected={value === rating}
            onClick={() => onChange(rating)}
            color={color}
          />
        ))}
      </div>
    </div>
  );
}

function Section({ section, formData, setFormData }) {
  return (
    <div className="mb-6">
      <div className="bg-[#8B7355] text-white px-4 py-3 rounded-t font-semibold">
        {section.title}
      </div>
      <div className="bg-white px-4 py-4 border border-gray-200 rounded-b">
        <div className="text-xs text-gray-500 mb-4">
          0 = Never, 1 = Sometimes, 2 = Usually, 3 = Regularly, 4 = Always
        </div>
        {section.questions.map((question) => (
          <QuestionRow
            key={question.key}
            question={question}
            value={formData[question.key]}
            onChange={(value) => setFormData({ ...formData, [question.key]: value })}
            color={section.color}
          />
        ))}
      </div>
    </div>
  );
}

export default function EvaluationForm() {
  const [currentTeammateIndex, setCurrentTeammateIndex] = useState(0);
  const [evaluations, setEvaluations] = useState([]);
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [formData, setFormData] = useState(() => {
    const initial = { feedback: '' };
    evaluationSections.forEach(section => {
      section.questions.forEach(q => {
        initial[q.key] = null;
      });
    });
    return initial;
  });

  const currentTeammate = mockTeammates[currentTeammateIndex];
  const progress = (evaluations.length / mockTeammates.length) * 100;

  const validateForm = () => {
    const missingFields = [];
    evaluationSections.forEach(section => {
      section.questions.forEach(q => {
        if (formData[q.key] === null) {
          missingFields.push(q.text);
        }
      });
    });

    if (missingFields.length > 0) {
      setErrorMessage(`Please provide ratings for: ${missingFields.slice(0, 3).join(', ')}${missingFields.length > 3 ? '...' : ''}`);
      setShowError(true);
      return false;
    }

    if (formData.feedback.trim().length < 20) {
      setErrorMessage('Please provide at least 20 characters of written feedback');
      setShowError(true);
      return false;
    }

    return true;
  };

  const handleSubmit = () => {
    if (!validateForm()) return;

    const evaluation = {
      teammateId: currentTeammate.id,
      teammateName: currentTeammate.name,
      ...formData,
      submittedAt: new Date().toISOString()
    };

    setEvaluations([...evaluations, evaluation]);
    console.log('Evaluation submitted:', evaluation);

    if (currentTeammateIndex < mockTeammates.length - 1) {
      setCurrentTeammateIndex(currentTeammateIndex + 1);
      resetForm();
    } else {
      setShowSuccess(true);
    }
  };

  const resetForm = () => {
    const initial = { feedback: '' };
    evaluationSections.forEach(section => {
      section.questions.forEach(q => {
        initial[q.key] = null;
      });
    });
    setFormData(initial);
  };

  if (showSuccess) {
    return (
      <Container className="py-5">
        <Card className="text-center p-5">
          <div className="text-6xl mb-4">âœ…</div>
          <h2 className="mb-3">All Evaluations Submitted!</h2>
          <p className="text-gray-600">You have completed evaluations for all {mockTeammates.length} teammates.</p>
          <Button 
            onClick={() => {
              setShowSuccess(false);
              setCurrentTeammateIndex(0);
              setEvaluations([]);
              resetForm();
            }}
            style={{ backgroundColor: '#8B7355', borderColor: '#8B7355' }}
            className="mt-3"
          >
            Submit Another Set
          </Button>
        </Card>
      </Container>
    );
  }

  return (
    <>
      <div 
        className="text-white text-center py-5"
        style={{
          backgroundImage: 'url(https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=1200)',
          backgroundSize: 'cover',
          backgroundPosition: 'center'
        }}
      >
        <h1 className="text-4xl font-bold mb-2">Peer Evaluation</h1>
      </div>

      <Container className="py-4" style={{ maxWidth: '900px' }}>
        <div className="bg-[#8B7355] text-white px-4 py-3 mb-4 rounded">
          <div className="d-flex justify-content-between align-items-center">
            <span className="font-semibold">Evaluating: {currentTeammate.name}</span>
            <span className="text-sm">Progress: {evaluations.length}/{mockTeammates.length}</span>
          </div>
        </div>

        {evaluationSections.map((section) => (
          <Section
            key={section.title}
            section={section}
            formData={formData}
            setFormData={setFormData}
          />
        ))}

        <div className="mb-4">
          <div className="bg-[#8B7355] text-white px-4 py-3 rounded-t font-semibold">
            Overall
          </div>
          <div className="bg-white px-4 py-4 border border-gray-200 rounded-b">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Additional Comments
            </label>
            <textarea
              rows={4}
              className="w-full px-3 py-2 border border-gray-300 rounded"
              placeholder="Provide specific examples of this teammate's contributions, strengths, and areas for improvement..."
              value={formData.feedback}
              onChange={(e) => setFormData({ ...formData, feedback: e.target.value })}
            />
            <div className="text-xs text-gray-500 mt-1">
              Minimum 20 characters ({formData.feedback.length}/20)
            </div>
          </div>
        </div>

        <button
          onClick={handleSubmit}
          className="w-full bg-[#8B7355] hover:bg-[#6d5a42] text-white py-3 rounded font-semibold text-lg transition-colors"
        >
          {currentTeammateIndex < mockTeammates.length - 1 ? 'NEXT PEER' : 'SUBMIT ALL EVALUATIONS'}
        </button>

        <p className="text-center text-sm text-gray-500 mt-4">
          Note: If you have any issues completing this form, please contact an admin.
        </p>
      </Container>

      <Modal show={showError} onHide={() => setShowError(false)} centered>
        <Modal.Body className="text-center p-5" style={{ backgroundColor: '#C97C7C' }}>
          <p className="text-white mb-4">{errorMessage}</p>
          <Button 
            onClick={() => setShowError(false)}
            style={{ backgroundColor: 'white', color: '#C97C7C', border: 'none' }}
          >
            OK
          </Button>
        </Modal.Body>
      </Modal>
    </>
  );
}
