import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

// --- Color Palette (matching other components) ---
const COLORS = {
  HEADER_TEXT: '#2d3748', 
  SUB_HEADER_TEXT: '#5C7094', 
  UNDERLINE_BLUE: '#5C7094', 
  HEADER_BAR_BG: '#e8e8e8', 
  TAN_BASE: '#C4B5A0',
  TAN_SELECTED: '#8B7355',
  NAVY_BASE: '#5C7094',
  NAVY_SELECTED: '#4A5568',
  TEXT_PRIMARY: '#4A5568',     
  TEXT_SECONDARY: '#A0AEC0',    
  SECTION_TITLE_COLOR: '#2d3748',
  WHITE: 'white',
  BODY_BACKGROUND: 'white',  
  ERROR_RED: '#C97C7C',
  PROGRESS_GRAY: '#ccc',
  NAVY_DARK: '#001f44',
};

export default function Login() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    role: 'student'
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      // Simple authentication logic (in production, this would call an API)
      if (formData.username && formData.password) {
        await login(formData.role, formData.username);

        // Navigate based on role
        if (formData.role === 'student') {
          navigate('/evaluation');
        } else {
          navigate('/dashboard');
        }
      } else {
        setError('Please enter both username and password');
      }
    } catch (err) {
      setError('Login failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const outerContainerStyle = {
    fontFamily: 'Georgia, serif', 
    backgroundColor: COLORS.BODY_BACKGROUND, 
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  };

  const loginContainerStyle = {
    backgroundColor: COLORS.WHITE,
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    padding: '40px',
    width: '100%',
    maxWidth: '400px',
    borderTop: `4px solid ${COLORS.TAN_SELECTED}`,
  };

  const titleStyle = {
    fontSize: '28px',
    fontWeight: 'bold',
    color: COLORS.SECTION_TITLE_COLOR,
    textAlign: 'center',
    marginBottom: '30px',
    fontFamily: 'Georgia, serif',
  };

  const formGroupStyle = {
    marginBottom: '20px',
  };

  const labelStyle = {
    display: 'block',
    fontSize: '14px',
    fontWeight: '500',
    color: COLORS.TEXT_PRIMARY,
    marginBottom: '8px',
    fontFamily: 'Georgia, serif',
  };

  const inputStyle = {
    width: '100%',
    padding: '12px 16px',
    border: '1px solid #ccc',
    borderRadius: '4px',
    fontSize: '14px',
    fontFamily: 'Georgia, serif',
    boxSizing: 'border-box',
  };

  const selectStyle = {
    ...inputStyle,
    cursor: 'pointer',
  };

  const buttonStyle = {
    width: '100%',
    backgroundColor: COLORS.TAN_SELECTED,
    color: COLORS.WHITE,
    padding: '12px 20px',
    borderRadius: '4px',
    fontWeight: '600',
    border: 'none',
    cursor: 'pointer',
    fontSize: '16px',
    fontFamily: 'Georgia, serif',
    transition: 'background-color 0.2s',
  };

  const errorStyle = {
    backgroundColor: COLORS.ERROR_RED,
    color: COLORS.WHITE,
    padding: '12px 16px',
    fontSize: '14px',
    textAlign: 'center',
    fontWeight: '500',
    fontFamily: 'Georgia, serif',
    borderRadius: '4px',
    marginBottom: '20px',
  };

  const roleSelectorStyle = {
    display: 'flex',
    gap: '10px',
    marginBottom: '20px',
  };

  const roleButtonStyle = (isSelected) => ({
    flex: 1,
    padding: '10px 16px',
    backgroundColor: isSelected ? COLORS.TAN_SELECTED : COLORS.TAN_BASE,
    color: COLORS.WHITE,
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '14px',
    fontWeight: '500',
    fontFamily: 'Georgia, serif',
    transition: 'background-color 0.2s',
  });

  return (
    <div style={outerContainerStyle}>
      <div style={loginContainerStyle}>
        <h1 style={titleStyle}>SMU Peer Evaluation</h1>

        {error && <div style={errorStyle}>{error}</div>}

        <form onSubmit={handleSubmit}>
          {/* Role Selection */}
          <div style={formGroupStyle}>
            <label style={labelStyle}>Login As:</label>
            <div style={roleSelectorStyle}>
              <button
                type="button"
                style={roleButtonStyle(formData.role === 'student')}
                onClick={() => setFormData(prev => ({ ...prev, role: 'student' }))}
              >
                Student
              </button>
              <button
                type="button"
                style={roleButtonStyle(formData.role === 'professor')}
                onClick={() => setFormData(prev => ({ ...prev, role: 'professor' }))}
              >
                Professor
              </button>
            </div>
          </div>

          {/* Username */}
          <div style={formGroupStyle}>
            <label style={labelStyle} htmlFor="username">
              Username:
            </label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleInputChange}
              style={inputStyle}
              placeholder="Enter your username"
              required
            />
          </div>

          {/* Password */}
          <div style={formGroupStyle}>
            <label style={labelStyle} htmlFor="password">
              Password:
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              style={inputStyle}
              placeholder="Enter your password"
              required
            />
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            style={buttonStyle}
            disabled={isLoading}
            onMouseOver={(e) => !isLoading && (e.target.style.backgroundColor = COLORS.NAVY_SELECTED)}
            onMouseOut={(e) => !isLoading && (e.target.style.backgroundColor = COLORS.TAN_SELECTED)}
          >
            {isLoading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        {/* Demo Credentials */}
        <div style={{ marginTop: '30px', padding: '20px', backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
          <h4 style={{ fontSize: '14px', fontWeight: 'bold', color: COLORS.TEXT_PRIMARY, marginBottom: '10px' }}>
            Demo Credentials:
          </h4>
          <p style={{ fontSize: '12px', color: COLORS.TEXT_SECONDARY, margin: '5px 0' }}>
            <strong>Student:</strong> Any username/password
          </p>
          <p style={{ fontSize: '12px', color: COLORS.TEXT_SECONDARY, margin: '5px 0' }}>
            <strong>Professor:</strong> Any username/password
          </p>
        </div>
      </div>
    </div>
  );
}
