import React, { useState } from 'react';
import { Form, Button, Container, Card } from 'react-bootstrap';

// --- Color Palette and Constants (Matching EvaluationForm.js) ---
const COLORS = {
  // Tan and Navy from the evaluation form
  TAN_BASE: '#C4B5A0',
  NAVY_SELECTED: '#4A5568',
  NAVY_DARK: '#001f44', // Dark Blue from top nav
  WHITE: 'white',
  TEXT_PRIMARY: '#4A5568',
  BODY_BACKGROUND: '#f8f8f8', // Light background for contrast
};

// --- Custom Styles for Login Page ---
const loginStyles = {
    // The main container background to match the overall design feel
    outerContainer: {
        minHeight: '100vh',
        backgroundColor: COLORS.BODY_BACKGROUND,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        fontFamily: 'Georgia, serif',
    },
    // The header banner section
    headerBanner: {
        backgroundImage: `linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.3)), url(/images/header-background.png)`, // Reusing the same header image
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        width: '100%',
        padding: '50px 0 10px 0',
        color: COLORS.WHITE,
        position: 'relative',
    },
    titleStyle: {
        fontSize: '30px',
        fontWeight: 'normal',
        margin: 0,
        padding: '0 40px',
    },
    // The card that holds the login form
    loginCard: {
        maxWidth: '400px',
        width: '90%',
        padding: '40px 30px',
        backgroundColor: COLORS.WHITE,
        borderRadius: '8px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
        marginTop: '-30px', // Lift card up into the header banner area
        borderTop: `4px solid ${COLORS.NAVY_SELECTED}`,
        textAlign: 'left',
    },
    // Input fields styling to match the tan color from the design
    inputStyle: {
        backgroundColor: COLORS.TAN_BASE,
        border: '1px solid ' + COLORS.TAN_BASE,
        color: COLORS.NAVY_DARK,
        height: '45px',
        padding: '10px 15px',
        fontSize: '16px',
        marginBottom: '15px',
        borderRadius: '4px',
    },
    // Login button styling to match the dark navy color
    buttonStyle: {
        backgroundColor: COLORS.NAVY_DARK,
        color: COLORS.WHITE,
        padding: '10px 20px',
        fontSize: '18px',
        fontWeight: 'bold',
        borderRadius: '4px',
        border: 'none',
        width: '100%',
        marginTop: '20px',
        cursor: 'pointer',
        transition: 'background-color 0.2s',
    },
    headerText: {
        fontSize: '28px',
        fontWeight: 'bold',
        marginBottom: '25px',
        color: COLORS.TEXT_PRIMARY,
        textAlign: 'center',
    }
};

export default function LoginPage({ onLoginSuccess }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        if (!username || !password) {
            setError('Username and password are required.');
            return;
        }

        // --- MOCK AUTHENTICATION ---
        // In a real application, you would send a request to your backend API here:
        // const response = await fetch('/api/login', { method: 'POST', body: JSON.stringify({ username, password }) });

        // For this mock, we'll just check for a hardcoded pair.
        if (username === 'student' && password === 'peer') {
            console.log("Login Successful!");
            // Call the function passed from the parent component to switch views
            onLoginSuccess({ username: username }); 
        } else {
            setError('Invalid username or password.');
        }
    };

    return (
        <div style={loginStyles.outerContainer}>
            {/* Mock Top Nav Bar - Simple Version */}
            <div style={{ 
                width: '100%', 
                backgroundColor: COLORS.NAVY_DARK, 
                color: COLORS.WHITE, 
                padding: '10px 20px', 
                fontSize: '18px', 
                fontWeight: 'bold', 
                display: 'flex', 
                justifyContent: 'space-between' 
            }}>
                <span>SMU Peer Evaluation</span>
                <span>Menu â˜°</span>
            </div>

            {/* Header Banner - Student Home */}
            <div style={loginStyles.headerBanner}>
                <h1 style={loginStyles.titleStyle}>Student Home</h1>
            </div>

            <Container className="d-flex justify-content-center" style={{ width: '100%' }}>
                <Card style={loginStyles.loginCard}>
                    <h2 style={loginStyles.headerText}>Login</h2>
                    <Form onSubmit={handleLogin}>
                        {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

                        <Form.Group controlId="formBasicUsername">
                            <Form.Label style={{ color: COLORS.TEXT_PRIMARY, fontWeight: 'bold' }}>Username</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Enter Username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                style={loginStyles.inputStyle}
                                required
                            />
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword">
                            <Form.Label style={{ color: COLORS.TEXT_PRIMARY, fontWeight: 'bold' }}>Password</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Enter Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                style={loginStyles.inputStyle}
                                required
                            />
                        </Form.Group>

                        <Button variant="primary" type="submit" style={loginStyles.buttonStyle}>
                            LOG IN
                        </Button>
                    </Form>
                </Card>
            </Container>
        </div>
    );
}
