import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import API_URL from '../config';

const COLORS = {
  TAN_BACKGROUND: 'rgba(138,112,76,0.9)',
  WHITE: 'white',
  NAVY_BUTTON: '#141b4d',
  PLACEHOLDER_TEXT: '#8a704c',
  TEXT_PRIMARY: '#2d3748',
  TEXT_SECONDARY: '#5C7094',
  ERROR_RED: '#C97C7C',
  SUCCESS_GREEN: '#4CAF50',
  BODY_BACKGROUND: 'white',
  HEADER_TEXT: '#2d3748',
  HEADER_BAR_BG: '#e8e8e8',
  TAN_BASE: '#C4B5A0',
  TAN_SELECTED: '#8B7355',
  NAVY_BASE: '#5C7094',
  NAVY_SELECTED: '#4A5568',
};

export default function EvaluationAssignment() {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [course, setCourse] = useState(null);
  const [groups, setGroups] = useState([]);
  const [students, setStudents] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [description, setDescription] = useState('');
  const [points, setPoints] = useState('25');
  const [assignToType, setAssignToType] = useState('groups'); // 'everyone' or 'groups'
  const [selectedGroups, setSelectedGroups] = useState([]);
  const [selectedStudents, setSelectedStudents] = useState([]);
  const [dueDate, setDueDate] = useState('');
  const [dueTime, setDueTime] = useState('');
  const [availableFromDate, setAvailableFromDate] = useState('');
  const [availableFromTime, setAvailableFromTime] = useState('');
  const [untilDate, setUntilDate] = useState('');
  const [untilTime, setUntilTime] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [showGroupDropdown, setShowGroupDropdown] = useState(false);
  const [showStudentDropdown, setShowStudentDropdown] = useState(false);
  const [pendingAssignments, setPendingAssignments] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const fetchCourseInfo = useCallback(async () => {
    try {
      const response = await fetch(`${API_URL}/api/courses/${courseId}`);
      const data = await response.json();
      if (response.ok) {
        setCourse(data.course);
      }
    } catch (err) {
      console.error('Error fetching course:', err);
    }
  }, [courseId]);

  const fetchGroups = useCallback(async () => {
    try {
      const response = await fetch(`${API_URL}/api/courses/${courseId}/groups`);
      const data = await response.json();
      if (response.ok) {
        setGroups(data.groups || []);
      }
    } catch (err) {
      console.error('Error fetching groups:', err);
    }
  }, [courseId]);

  const fetchStudents = useCallback(async () => {
    try {
      const response = await fetch(`${API_URL}/api/courses/${courseId}/roster`);
      const data = await response.json();
      if (response.ok) {
        setStudents(data.roster || []);
      }
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  }, [courseId]);

  const fetchAssignments = useCallback(async () => {
    try {
      const response = await fetch(`${API_URL}/api/courses/${courseId}/evaluation-assignments`);
      const data = await response.json();
      if (response.ok) {
        setAssignments(data.assignments || []);
      }
    } catch (err) {
      console.error('Error fetching assignments:', err);
    }
  }, [courseId]);

  useEffect(() => {
    const loadData = async () => {
      setIsLoading(true);
      await Promise.all([
        fetchCourseInfo(),
        fetchGroups(),
        fetchStudents(),
        fetchAssignments()
      ]);
      setIsLoading(false);
    };
    loadData();
  }, [courseId, fetchCourseInfo, fetchGroups, fetchStudents, fetchAssignments]);

  const handleGroupSelect = (group) => {
    setSelectedGroup(group);
    setSelectedStudents([]);
  };

  const handleStudentToggle = (studentId) => {
    setSelectedStudents(prev => {
      if (prev.includes(studentId)) {
        return prev.filter(id => id !== studentId);
      } else {
        return [...prev, studentId];
      }
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedGroup) {
      setError('Please select a group');
      return;
    }

    if (selectedStudents.length === 0) {
      setError('Please select at least one student to assign the evaluation');
      return;
    }

    if (!dueDate) {
      setError('Please select a due date');
      return;
    }

    setIsSubmitting(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch(`${API_URL}/api/evaluation-assignments`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          courseId: parseInt(courseId),
          groupId: selectedGroup.groupid,
          evaluatorStudentIds: selectedStudents,
          dueDate: new Date(dueDate).toISOString(),
          assignmentName: assignmentName || null
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccess(`Successfully created ${data.assignments.length} evaluation assignment(s)`);
        setSelectedGroup(null);
        setSelectedStudents([]);
        setDueDate('');
        setAssignmentName('');
        fetchAssignments();
      } else {
        setError(data.message || 'Failed to create assignment');
      }
    } catch (err) {
      console.error('Error creating assignment:', err);
      setError('Failed to create assignment. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDeleteAssignment = async (assignmentId) => {
    if (!window.confirm('Are you sure you want to delete this assignment?')) {
      return;
    }

    try {
      const response = await fetch(`${API_URL}/api/evaluation-assignments/${assignmentId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        setSuccess('Assignment deleted successfully');
        fetchAssignments();
      } else {
        const data = await response.json();
        setError(data.message || 'Failed to delete assignment');
      }
    } catch (err) {
      console.error('Error deleting assignment:', err);
      setError('Failed to delete assignment. Please try again.');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return COLORS.SUCCESS_GREEN;
      case 'overdue':
        return COLORS.ERROR_RED;
      default:
        return COLORS.TEXT_SECONDARY;
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const containerStyle = {
    fontFamily: 'Georgia, serif',
    backgroundColor: COLORS.BODY_BACKGROUND,
    minHeight: '100vh',
    padding: '40px 20px',
  };

  const cardStyle = {
    backgroundColor: COLORS.WHITE,
    borderRadius: '12px',
    padding: '30px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    maxWidth: '1200px',
    margin: '0 auto',
    marginBottom: '20px',
  };

  const titleStyle = {
    fontSize: '28px',
    fontWeight: 'bold',
    color: COLORS.TEXT_PRIMARY,
    marginBottom: '20px',
  };

  const sectionStyle = {
    marginBottom: '30px',
    padding: '20px',
    backgroundColor: '#f8f9fa',
    borderRadius: '8px',
  };

  const sectionTitleStyle = {
    fontSize: '20px',
    fontWeight: '600',
    color: COLORS.TEXT_PRIMARY,
    marginBottom: '15px',
  };

  const buttonStyle = {
    backgroundColor: COLORS.NAVY_BUTTON,
    color: COLORS.WHITE,
    padding: '12px 24px',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: '600',
    border: 'none',
    cursor: 'pointer',
    fontFamily: 'Georgia, serif',
    transition: 'background-color 0.2s',
  };

  const deleteButtonStyle = {
    ...buttonStyle,
    backgroundColor: COLORS.ERROR_RED,
    padding: '6px 12px',
    fontSize: '12px',
  };

  const inputStyle = {
    width: '100%',
    padding: '10px',
    borderRadius: '4px',
    border: `1px solid ${COLORS.TAN_BASE}`,
    fontSize: '16px',
    fontFamily: 'Georgia, serif',
    marginBottom: '10px',
  };

  const checkboxStyle = {
    marginRight: '10px',
    cursor: 'pointer',
  };

  const studentItemStyle = {
    padding: '10px',
    marginBottom: '8px',
    border: `1px solid ${COLORS.TAN_BASE}`,
    borderRadius: '4px',
    backgroundColor: COLORS.WHITE,
    display: 'flex',
    alignItems: 'center',
  };

  const selectedStudentItemStyle = {
    ...studentItemStyle,
    borderColor: COLORS.NAVY_BUTTON,
    backgroundColor: '#f0f4ff',
  };

  if (isLoading) {
    return (
      <div style={containerStyle}>
        <div style={cardStyle}>
          <div style={{ textAlign: 'center', padding: '40px' }}>
            Loading...
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={containerStyle}>
      <div style={cardStyle}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h1 style={titleStyle}>
            {course ? `Evaluation Assignments - ${course.course_name}` : 'Evaluation Assignments'}
          </h1>
          <button
            onClick={() => navigate(`/course-roster/${courseId}`)}
            style={buttonStyle}
          >
            Back to Course
          </button>
        </div>

        {error && (
          <div style={{ backgroundColor: '#fee', color: COLORS.ERROR_RED, padding: '15px', borderRadius: '8px', marginBottom: '20px' }}>
            {error}
          </div>
        )}

        {success && (
          <div style={{ backgroundColor: '#efe', color: COLORS.SUCCESS_GREEN, padding: '15px', borderRadius: '8px', marginBottom: '20px' }}>
            {success}
          </div>
        )}

        {/* Create New Assignment Section */}
        <div style={sectionStyle}>
          <h2 style={sectionTitleStyle}>Create New Evaluation Assignment</h2>

          <form onSubmit={handleSubmit}>
            {/* Group Selection */}
            <div style={{ marginBottom: '20px' }}>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600' }}>
                Select Group to Evaluate:
              </label>
              {groups.length === 0 ? (
                <div style={{ padding: '20px', textAlign: 'center', color: COLORS.TEXT_SECONDARY }}>
                  No groups available. Create groups first.
                </div>
              ) : (
                <div>
                  {groups.map((group) => (
                    <div
                      key={group.groupid}
                      onClick={() => handleGroupSelect(group)}
                      style={selectedGroup?.groupid === group.groupid ? {
                        ...studentItemStyle,
                        borderColor: COLORS.NAVY_BUTTON,
                        backgroundColor: '#f0f4ff',
                        cursor: 'pointer'
                      } : {
                        ...studentItemStyle,
                        cursor: 'pointer'
                      }}
                    >
                      <strong>{group.group_name}</strong>
                      <span style={{ marginLeft: '10px', color: COLORS.TEXT_SECONDARY }}>
                        ({group.student_count || 0} members)
                      </span>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Student Selection (Evaluators) */}
            {selectedGroup && (
              <div style={{ marginBottom: '20px' }}>
                <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600' }}>
                  Select Students to Assign This Evaluation:
                </label>
                <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
                  {students.map((student) => (
                    <div
                      key={student.studentid}
                      style={selectedStudents.includes(student.studentid) ? selectedStudentItemStyle : studentItemStyle}
                      onClick={() => handleStudentToggle(student.studentid)}
                    >
                      <input
                        type="checkbox"
                        checked={selectedStudents.includes(student.studentid)}
                        onChange={() => handleStudentToggle(student.studentid)}
                        style={checkboxStyle}
                      />
                      <span>{student.studentname}</span>
                      {student.email && (
                        <span style={{ marginLeft: '10px', color: COLORS.TEXT_SECONDARY, fontSize: '14px' }}>
                          ({student.email})
                        </span>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Assignment Name */}
            <div style={{ marginBottom: '20px' }}>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600' }}>
                Assignment Name (Optional):
              </label>
              <input
                type="text"
                value={assignmentName}
                onChange={(e) => setAssignmentName(e.target.value)}
                placeholder="e.g., Midterm Peer Evaluation"
                style={inputStyle}
              />
            </div>

            {/* Due Date */}
            <div style={{ marginBottom: '20px' }}>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: '600' }}>
                Due Date:
              </label>
              <input
                type="datetime-local"
                value={dueDate}
                onChange={(e) => setDueDate(e.target.value)}
                style={inputStyle}
                required
              />
            </div>

            <button
              type="submit"
              disabled={isSubmitting || !selectedGroup || selectedStudents.length === 0 || !dueDate}
              style={{
                ...buttonStyle,
                opacity: (isSubmitting || !selectedGroup || selectedStudents.length === 0 || !dueDate) ? 0.6 : 1,
                cursor: (isSubmitting || !selectedGroup || selectedStudents.length === 0 || !dueDate) ? 'not-allowed' : 'pointer'
              }}
            >
              {isSubmitting ? 'Creating...' : 'Create Assignment'}
            </button>
          </form>
        </div>

        {/* Existing Assignments Section */}
        <div style={sectionStyle}>
          <h2 style={sectionTitleStyle}>Existing Assignments</h2>

          {assignments.length === 0 ? (
            <div style={{ padding: '20px', textAlign: 'center', color: COLORS.TEXT_SECONDARY }}>
              No assignments created yet.
            </div>
          ) : (
            <div>
              {assignments.map((assignment) => (
                <div
                  key={assignment.assignmentid}
                  style={{
                    ...studentItemStyle,
                    borderLeft: `4px solid ${getStatusColor(assignment.status)}`,
                  }}
                >
                  <div style={{ flex: 1 }}>
                    <div style={{ fontWeight: '600', marginBottom: '5px' }}>
                      {assignment.assignment_name || 'Peer Evaluation'}
                    </div>
                    <div style={{ fontSize: '14px', color: COLORS.TEXT_SECONDARY, marginBottom: '5px' }}>
                      <strong>Evaluator:</strong> {assignment.evaluator_name}
                      {assignment.evaluator_email && ` (${assignment.evaluator_email})`}
                    </div>
                    <div style={{ fontSize: '14px', color: COLORS.TEXT_SECONDARY, marginBottom: '5px' }}>
                      <strong>Group:</strong> {assignment.group_name}
                    </div>
                    <div style={{ fontSize: '14px', color: COLORS.TEXT_SECONDARY, marginBottom: '5px' }}>
                      <strong>Due:</strong> {formatDate(assignment.due_date)}
                    </div>
                    <div style={{ fontSize: '14px', color: COLORS.TEXT_SECONDARY }}>
                      <strong>Status:</strong>{' '}
                      <span style={{ color: getStatusColor(assignment.status) }}>
                        {assignment.status.charAt(0).toUpperCase() + assignment.status.slice(1)}
                      </span>
                      {assignment.completed_at && (
                        <span style={{ marginLeft: '10px' }}>
                          (Completed: {formatDate(assignment.completed_at)})
                        </span>
                      )}
                    </div>
                  </div>
                  <button
                    onClick={() => handleDeleteAssignment(assignment.assignmentid)}
                    style={deleteButtonStyle}
                  >
                    Delete
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
