            <h1 style={evalTitleStyle}>
              Evaluating: {currentTeammate.name}
              <span style={evalTitleUnderlineStyle}></span>
            </h1>
          </div>
          <span style={projectManagementStyle}>Project Management</span>
        </div>
      </div>

      <div style={containerStyle}> {/* The "paper" area */}
