import React, { useState } from 'react';
import { Container, Form, Row, Col } from 'react-bootstrap';

// --- Color Palette and Constants (Unchanged) ---
const COLORS = {
  HEADER_BROWN: '#8B7355',
  LIGHT_TAN: '#C4B5A0',
  DARK_NAVY: '#4A5568',
  SELECTED_BROWN: '#8B7355',
  BASE_BUTTON: '#C4B5A0',
  TEXT_GRAY: '#4A5568',
  LIGHT_GRAY_TEXT: '#A0AEC0',
  WHITE: 'white',
  BLACK: 'black',
  BLUE_BORDER: '#5C7094',
  ERROR_RED: '#C97C7C',
};

// --- Mock Data (Unchanged) ---
const mockTeammates = [
  { id: 1, name: "Ben Holt" },
  { id: 2, name: "Zack Mitchell" },
  { id: 3, name: "Ashley Gutierrez" }
];

const evaluationCriteria = [
