  };


return (
  <div style={{ fontFamily: 'Georgia, serif', backgroundColor: '#f0f0f0' }}>
    {/* Header Banner with Rounded Corner */}
    <div style={headerBannerStyle}>
      <div style={titleBoxStyle}>
        <h1 style={{ fontSize: '30px', fontWeight: 'normal', margin: 0, color: COLORS.WHITE }}>Peer Evaluation</h1>
        <div style={{ fontSize: '14px', color: COLORS.WHITE }}>
            <span>{evaluations.length}/{mockTeammates.length} completed</span>
        </div>
      </div>
      {/* Progress Bar/Status Bar */}
      <div style={statusBarStyle}></div>
    </div>

    <div style={containerStyle}>

      {/* Team Info Box */}
      <div style={teamInfoBoxStyle}>
        <span>Evaluating: {currentTeammate.name}</span>
        <span>Project Management</span>
      </div>

      {/* Introduction and Key */}
      <div style={keyParagraphStyle}>
        <p style={{ margin: 0 }}>
          Teamwork is behaviors and under control of the individual team members. In your experience, how often does your peer
          demonstrate the following? Rate the frequency of the teamwork behavior for each of your group members by choosing the
          corresponding number which may fall in the boxes provided.
        </p>
      </div>

      <Form onSubmit={handleSubmit}>
        {evaluationCriteria.map(({ key, label, scaleType, headerColor }) => (
          <RatingInput
            key={key}
            label={label}
            name={key}
            value={formData[key]}
            onChange={(value) => setFormData({ ...formData, [key]: value })}
            error={errors[key]}
            scaleType={scaleType}
            headerColor={headerColor}
          />
        ))}

        {/* Written Feedback Section */}
        <div style={{ marginBottom: '20px' }}>
          <div style={{ ...teamInfoBoxStyle, backgroundColor: COLORS.LIGHT_TAN, marginBottom: '0', border: '1px solid #ccc', borderBottom: 'none' }}>
            <span>Overall</span>
            <span>Additional Comments</span>
          </div>
          <div style={{ backgroundColor: COLORS.WHITE, padding: '15px 15px', border: '1px solid #ccc', borderTop: 'none' }}>
            <Form.Group>
              <Form.Control
                as="textarea"
                rows={4}
                style={textareaStyle}
                placeholder="Provide specific examples of this teammate's contributions, strengths, and areas for improvement..."
                value={formData.feedback}
                onChange={(e) => setFormData({ ...formData, feedback: e.target.value })}
              />
              <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '4px' }}>
                <span style={{ fontSize: '12px', color: COLORS.LIGHT_GRAY_TEXT }}>
                  Minimum 20 characters ({formData.feedback.length}/20)
                </span>
                {errors.feedback && <div style={{ color: COLORS.ERROR_RED, fontSize: '12px' }}>{errors.feedback}</div>}
              </div>
            </Form.Group>
          </div>
        </div>


        {/* Navigation Buttons */}
        <div style={{ display: 'flex', justifyContent: 'space-between', paddingTop: '10px' }}>
          <button
            type="button"
            style={{ ...submitButtonStyle, backgroundColor: COLORS.TEXT_GRAY }}
            disabled={currentTeammateIndex === 0}
            onClick={() => {
              setCurrentTeammateIndex(currentTeammateIndex - 1);
              resetForm();
            }}
          >
            PREVIOUS
          </button>
          <button type="submit" style={submitButtonStyle}>
            {currentTeammateIndex < mockTeammates.length - 1 ? 'NEXT PEER' : 'SUBMIT ALL EVALUATIONS'}
          </button>
        </div>

        <p style={{ textAlign: 'center', fontSize: '12px', color: COLORS.LIGHT_GRAY_TEXT, marginTop: '20px' }}>
          Mark up all peer evaluations. Click on each checkbox that matches the question and write a sentence representative. Also navigate back and forth and end it if they have done all the evaluations.
        </p>
      </Form>
    </div>
  </div>
);
}
