import React, { useState, useEffect, useCallback } from 'react';
import API_URL from '../config';
import { useNavigate, useParams } from 'react-router-dom';

const COLORS = {
  TAN_BACKGROUND: 'rgba(138,112,76,0.9)',
  WHITE: 'white',
  NAVY_BUTTON: '#141b4d',
  PLACEHOLDER_TEXT: '#8a704c',
  TEXT_PRIMARY: '#2d3748',
  TEXT_SECONDARY: '#5C7094',
  ERROR_RED: '#C97C7C',
  SUCCESS_GREEN: '#4CAF50',
  BODY_BACKGROUND: 'white',
  HEADER_TEXT: '#2d3748',
  HEADER_BAR_BG: '#e8e8e8',
  TAN_BASE: '#C4B5A0',
  TAN_SELECTED: '#8B7355',
};

export default function CourseRoster() {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [csvFile, setCsvFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [roster, setRoster] = useState([]);
  const [courseInfo, setCourseInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const fetchCourseInfo = useCallback(async () => {
    try {
      const response = await fetch(`${API_URL}/api/courses/${courseId}`);
      const data = await response.json();
      if (response.ok) {
        setCourseInfo(data.course);
      }
    } catch (err) {
      console.error('Error fetching course info:', err);
    }
  }, [courseId]);

  const fetchRoster = useCallback(async () => {
    try {
      setIsLoading(true);
      const response = await fetch(`${API_URL}/api/courses/${courseId}/roster`);
      const data = await response.json();
      if (response.ok) {
        setRoster(data.roster || []);
      }
    } catch (err) {
      console.error('Error fetching roster:', err);
    } finally {
      setIsLoading(false);
    }
  }, [courseId]);

  useEffect(() => {
    fetchCourseInfo();
    fetchRoster();
  }, [fetchCourseInfo, fetchRoster]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && (file.type === 'text/csv' || file.name.endsWith('.csv'))) {
      setCsvFile(file);
      setUploadStatus('');
    } else {
      setUploadStatus('Please select a valid CSV file');
    }
  };

  const handleUpload = async () => {
    if (!csvFile) {
      setUploadStatus('Please select a CSV file first');
      return;
    }

    setIsUploading(true);
    setUploadStatus('');

    const formData = new FormData();
    formData.append('csvFile', csvFile);
    formData.append('courseId', courseId);

    try {
      const response = await fetch(`${API_URL}/api/courses/${courseId}/upload-roster`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setUploadStatus(
          `Success! Imported ${data.successCount} students. ${data.duplicateCount} duplicates skipped.`
        );
        setCsvFile(null);
        document.getElementById('csvFile').value = '';
        fetchRoster(); // Refresh roster
      } else {
        setUploadStatus(`Error: ${data.message}`);
      }
    } catch (err) {
      setUploadStatus('Error uploading file. Please try again.');
    } finally {
      setIsUploading(false);
    }
  };

  const outerContainerStyle = {
    fontFamily: 'Georgia, serif',
    backgroundColor: COLORS.BODY_BACKGROUND,
    minHeight: '100vh',
  };

  const headerBannerStyle = {
    backgroundImage: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url("https://www.kenan-flagler.unc.edu/wp-content/uploads/nmc-images/2019/10/singapore_skyline-width2000height772.jpg")`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    padding: '50px 0 10px 0',
    position: 'relative',
  };

  const titleContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    padding: '0 40px',
    width: '100%',
  };

  const titleStyle = {
    fontSize: '30px',
    fontWeight: 'normal',
    margin: 0,
    color: COLORS.WHITE,
    position: 'relative',
    display: 'inline-block',
    paddingBottom: '5px',
  };

  const mainContentStyle = {
    display: 'flex',
    padding: '20px',
    gap: '20px',
    maxWidth: '1400px',
    margin: '0 auto',
  };

  const leftPanelStyle = {
    flex: '0 0 300px',
    backgroundColor: COLORS.WHITE,
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    height: 'fit-content',
  };

  const rightPanelStyle = {
    flex: '1',
    backgroundColor: COLORS.WHITE,
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  };

  const sectionTitleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    color: COLORS.TEXT_PRIMARY,
    marginBottom: '20px',
    fontFamily: 'Georgia, serif',
  };

  const buttonStyle = {
    backgroundColor: COLORS.TAN_SELECTED,
    color: COLORS.WHITE,
    padding: '12px 24px',
    borderRadius: '4px',
    fontWeight: '600',
    border: 'none',
    cursor: 'pointer',
    fontSize: '16px',
    fontFamily: 'Georgia, serif',
    transition: 'background-color 0.2s',
    width: '100%',
    marginBottom: '10px',
  };

  const activeButtonStyle = {
    ...buttonStyle,
    backgroundColor: COLORS.NAVY_BUTTON,
  };

  const uploadAreaStyle = {
    border: `2px dashed ${COLORS.TAN_BASE}`,
    borderRadius: '8px',
    padding: '40px',
    textAlign: 'center',
    backgroundColor: '#fafafa',
    marginBottom: '20px',
  };

  const fileInputStyle = {
    marginBottom: '20px',
    padding: '10px',
    border: `1px solid ${COLORS.TAN_BASE}`,
    borderRadius: '4px',
    width: '100%',
    fontFamily: 'Georgia, serif',
  };

  const statusStyle = {
    padding: '12px',
    borderRadius: '4px',
    marginBottom: '20px',
    fontFamily: 'Georgia, serif',
    fontSize: '14px',
  };

  const successStatusStyle = {
    ...statusStyle,
    backgroundColor: '#d4edda',
    color: '#155724',
    border: '1px solid #c3e6cb',
  };

  const errorStatusStyle = {
    ...statusStyle,
    backgroundColor: '#f8d7da',
    color: '#721c24',
    border: '1px solid #f5c6cb',
  };

  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse',
    fontFamily: 'Georgia, serif',
    fontSize: '14px',
  };

  const thStyle = {
    backgroundColor: COLORS.HEADER_BAR_BG,
    padding: '12px',
    textAlign: 'left',
    borderBottom: `1px solid #ccc`,
    fontWeight: '600',
    color: COLORS.HEADER_TEXT,
  };

  const tdStyle = {
    padding: '12px',
    borderBottom: `1px solid #ccc`,
    color: COLORS.TEXT_PRIMARY,
  };

  return (
    <div style={outerContainerStyle}>
      {/* Header Banner */}
      <div style={headerBannerStyle}>
        <div style={titleContainerStyle}>
          <h1 style={titleStyle}>{courseInfo?.course_name || 'Course Roster'}</h1>
        </div>
      </div>

      {/* Main Content */}
      <div style={mainContentStyle}>
        {/* Left Panel - Navigation */}
        <div style={leftPanelStyle}>
          <button
            onClick={() => navigate('/dashboard')}
            style={buttonStyle}
          >
            Return
          </button>
          <button
            onClick={() => navigate(`/course-roster/${courseId}`)}
            style={activeButtonStyle}
          >
            Import Roster
          </button>
          <button
            onClick={() => navigate(`/manage-groups/${courseId}`)}
            style={buttonStyle}
          >
            Manage Groups
          </button>
          <button
            onClick={() => navigate(`/evaluation-assignments/${courseId}`)}
            style={buttonStyle}
          >
            Evaluation Assignments
          </button>
        </div>

        {/* Right Panel - Import and Roster */}
        <div style={rightPanelStyle}>
          <h2 style={sectionTitleStyle}>Import Roster</h2>

          <div style={uploadAreaStyle}>
            <p style={{ color: COLORS.TEXT_PRIMARY, marginBottom: '20px' }}>
              Upload a CSV file to import students into this course
            </p>

            <input
              type="file"
              id="csvFile"
              accept=".csv"
              onChange={handleFileChange}
              style={fileInputStyle}
            />

            <div style={{
              backgroundColor: '#f8f9fa',
              padding: '15px',
              borderRadius: '4px',
              fontSize: '12px',
              fontFamily: 'monospace',
              marginTop: '10px',
              border: '1px solid #e9ecef',
            }}>
              <strong>Expected CSV Format:</strong><br />
              studentname<br />
              John Doe<br />
              Jane Smith<br />
              Bob Johnson
            </div>

            <button
              onClick={handleUpload}
              disabled={isUploading || !csvFile}
              style={{
                ...buttonStyle,
                opacity: isUploading || !csvFile ? 0.6 : 1,
                cursor: isUploading || !csvFile ? 'not-allowed' : 'pointer',
              }}
            >
              {isUploading ? 'Uploading...' : 'Upload CSV'}
            </button>
          </div>

          {uploadStatus && (
            <div style={
              uploadStatus.includes('Success') || uploadStatus.includes('Imported')
                ? successStatusStyle
                : errorStatusStyle
            }>
              {uploadStatus}
            </div>
          )}

          <h2 style={{...sectionTitleStyle, marginTop: '30px'}}>Current Roster</h2>

          {isLoading ? (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              Loading roster...
            </div>
          ) : roster.length === 0 ? (
            <div style={{ textAlign: 'center', padding: '40px', color: COLORS.TEXT_SECONDARY }}>
              No students in roster. Import a CSV file to add students.
            </div>
          ) : (
            <table style={tableStyle}>
              <thead>
                <tr>
                  <th style={thStyle}>Student ID</th>
                  <th style={thStyle}>Name</th>
                </tr>
              </thead>
              <tbody>
                {roster.map((student) => (
                  <tr key={student.studentid}>
                    <td style={tdStyle}>{student.studentid}</td>
                    <td style={tdStyle}>{student.studentname}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}
