import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Your custom SMU styles
import { Container, Navbar, Nav, Row, Col } from 'react-bootstrap';

// Import all necessary view components
import LoginPage from './components/LoginPage.js';
import EvaluationForm from './components/EvaluationForm.js';
// We'll create this component next for the class selection page
import ClassHomePage from './components/ClassHomePage'; 


// Define view constants
const VIEWS = {
  LOGIN: 'login',
  CLASS_HOME: 'class_home',
  EVALUATION_FORM: 'evaluation_form',
};

function App() {
  const [currentView, setCurrentView] = useState(VIEWS.LOGIN);
  const [user, setUser] = useState(null);
  const [selectedClass, setSelectedClass] = useState(null);

  // --- Navigation Handlers ---

  const handleLoginSuccess = (userData) => {
    setUser(userData);
    setCurrentView(VIEWS.CLASS_HOME);
  };

  const handleClassSelect = (className) => {
    setSelectedClass(className);
    setCurrentView(VIEWS.EVALUATION_FORM);
  };

  const handleLogout = () => {
      setUser(null);
      setSelectedClass(null);
      setCurrentView(VIEWS.LOGIN);
  };

  // --- Render Current View ---

  let content;
  let showNavContent = true;

  switch (currentView) {
    case VIEWS.LOGIN:
      content = <LoginPage onLoginSuccess={handleLoginSuccess} />;
      showNavContent = false; // Hide the standard navbar content for the specialized login screen
      break;

    case VIEWS.CLASS_HOME:
      content = (
        <Container className="py-5">
          <Row className="justify-content-center">
            <Col md={10} lg={10}>
              <ClassHomePage onClassSelect={handleClassSelect} username={user?.username} />
            </Col>
          </Row>
        </Container>
      );
      break;

    case VIEWS.EVALUATION_FORM:
      content = (
        <Container className="py-0" fluid> {/* Use fluid for full width */}
            {/* The EvaluationForm component contains its own specialized header/progress bar */}
            <EvaluationForm />
        </Container>
      );
      break;

    default:
      content = <div>404: View Not Found</div>;
  }

  // --- Main App Return ---
  return (
    <div className="App">
      {/* SMU Navbar */}
      <Navbar expand="lg" style={{ backgroundColor: '#00205B' }} variant="dark">
        <Container>
          <Navbar.Brand 
            href="#" 
            className="fw-bold"
            onClick={() => setCurrentView(VIEWS.CLASS_HOME)} // Click to go back to class home
          >
            SMU Peer Evaluation
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="smu-navbar" />
          <Navbar.Collapse id="smu-navbar">
            <Nav className="ms-auto">
              {showNavContent && (
                  <>
                    <Nav.Link onClick={() => setCurrentView(VIEWS.CLASS_HOME)} className="text-light">Home</Nav.Link>
                    <Nav.Link onClick={handleLogout} className="text-light">Logout</Nav.Link>
                  </>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Main Content Area */}
      {content}

      {/* Footer (Only show if not on the full-page login screen) */}
      {showNavContent && (
        <footer className="text-center py-4 mt-5" style={{ backgroundColor: '#F5F5F5' }}>
          <p className="mb-0 text-muted">
            &copy; {new Date().getFullYear()} Demo. For Educational Project.
          </p>
        </footer>
      )}
    </div>
  );
}

export default App;
